import * as passwordless from './passwordless.js'

 const app = new Vue({
    el: '#app',
    data: {
        registration: {
            username: "Arnaud",
            challenge: btoa(window.crypto.randomUUID()),
            options: {
                authenticatorType: 'auto',
                userVerification: 'required',
                timeout: 60000,
                attestation: false,
            },
            result: null
        },
        authentication: {
            credentialId: null,
            challenge: btoa(window.crypto.randomUUID()),
            options: {
                authenticatorType: 'auto',
                userVerification: 'required',
                timeout: 60000,
            },
            result: null
        },
        verification: {
            publicKey: "",
            algorithm: "RS256",
            clientData: "",
            authenticatorData: "SZYN5YgOjGh0NBcPZHZgW4/krrmihjLHmVzzuoMdl2MFAAAAAQ==",
            signature: "",
            isValid: null
        }
    },
    methods: {
        newChallenge() {
            return window.crypto.randomUUID()
        },
        async register() {
            try {
                let res = await passwordless.register(this.registration.username, this.registration.challenge, this.registration.options)
                this.$buefy.toast.open({
                    message: 'Registered!',
                    type: 'is-success'
                })
                console.log(res)
                this.registration.result = res
                this.authentication.credentialId = res.credential.id
            }
            catch(e) {
                console.warn(e)
                this.$buefy.toast.open({
                    message: e,
                    type: 'is-danger'
                })
                this.registration.result = {}
            }
        },
        async login() {
            try {
                let res = await passwordless.login([this.authentication.credentialId], this.authentication.challenge, this.authentication.options)
                console.log(res)
                this.authentication.result = res
            }
            catch(e) {
                console.warn(e)
                this.$buefy.toast.open({
                    message: e,
                    type: 'is-danger'
                })
                this.authentication.result = {}
            }
        },
        async verifySignature() {
            try {
                this.verification.isValid = await passwordless.verify(this.verification)
            }
            catch(e) {
                console.warn(e)
                this.$buefy.toast.open({
                    message: e,
                    type: 'is-danger'
                })
                this.verification.isValid = false
            }
        },
        parseAuthData(authData) {
            return passwordless.parseAuthenticatorDataBase64(authData)
        },
        parseClientData(clientData) {
            return JSON.parse(window.atob(clientData))
        }
    }
 })